PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE bikes (
	id VARCHAR(255) NOT NULL, 
	firm VARCHAR(255), 
	model VARCHAR(255), 
	year INTEGER, 
	price VARCHAR(255), 
	disc_price VARCHAR(255), 
	image_url VARCHAR(500), 
	product_url VARCHAR(500), 
	frame VARCHAR(255), 
	motor VARCHAR(255), 
	battery VARCHAR(255), 
	fork VARCHAR(255), 
	rear_shox VARCHAR(255), 
	stem VARCHAR(255), 
	handelbar VARCHAR(255), 
	front_brake VARCHAR(255), 
	rear_brake VARCHAR(255), 
	shifter VARCHAR(255), 
	rear_der VARCHAR(255), 
	cassette VARCHAR(255), 
	chain VARCHAR(255), 
	crank_set VARCHAR(255), 
	front_wheel VARCHAR(255), 
	rear_wheel VARCHAR(255), 
	rims VARCHAR(255), 
	front_axle VARCHAR(255), 
	rear_axle VARCHAR(255), 
	spokes VARCHAR(255), 
	tubes VARCHAR(255), 
	front_tire VARCHAR(255), 
	rear_tire VARCHAR(255), 
	saddle VARCHAR(255), 
	seat_post VARCHAR(255), 
	clamp VARCHAR(255), 
	charger VARCHAR(255), 
	wheel_size VARCHAR(255), 
	headset VARCHAR(255), 
	brake_lever VARCHAR(255), 
	screen VARCHAR(255), 
	extras VARCHAR(255), 
	pedals VARCHAR(255), 
	bb VARCHAR(255), 
	gear_count VARCHAR(255), 
	weight VARCHAR(255), 
	size VARCHAR(255), 
	hub VARCHAR(255), 
	brakes VARCHAR(255), 
	tires VARCHAR(255), 
	PRIMARY KEY (id)
);
CREATE TABLE comparisons (
	id INTEGER NOT NULL, 
	slug VARCHAR(500), 
	bike_ids TEXT, 
	comparison_data TEXT, 
	created_at DATETIME, 
	PRIMARY KEY (id), 
	UNIQUE (slug)
);
CREATE TABLE compare_counts (
	id INTEGER NOT NULL, 
	bike_id VARCHAR(255), 
	count INTEGER, 
	last_updated DATETIME, 
	PRIMARY KEY (id), 
	UNIQUE (bike_id), 
	FOREIGN KEY(bike_id) REFERENCES bikes (id)
);
COMMIT;
